    }, 
    {
        "id": "4",
        "parentid": "1",
        "text": "Pizza Quattro Stagioni",
        "value": "$5.5"
    }, 
    {
        "id": "5",
        "parentid": "1",
        "text": "Pizza Frutti di Mare",
        "value": "7"
    }, 
    {
        "text": "Pizza",
        "id": "1",
        "parentid": "-1",
        "value": "$1.1"
    }, 
    {
        "id": "6",
        "text": "Pasta",
        "parentid": "-1",
        "value": "$0.9"
    }, 
    {
        "id": "7",
        "parentid": "6",
        "text": "Spaghetti Bolognese",
        "value": "$4"
    }, 
    {
        "id": "8",
        "text": "Spaghetti Carbonara",
        "parentid": "6",
        "value": "$5"
    }, 
    {
        "id": "9",
        "text": "Rigatoni all'Arrabiata",
        "parentid": "6",
        "value": "$5.5"
    }, 
    {
        "id": "10",
        "text": "Rigatoni al Forno",
        "parentid": "6",
        "value": "$5.5"
    }, 
    {
        "id": "11",
        "text": "Pappardelle con Porcini",
        "parentid": "6",
        "value": "$7"
    }, 
    {
        "id": "12",
        "text": "Salad",
        "parentid": "-1"
    }, 
    {
        "id": "13",
        "text": "Cesars Salad",
        "parentid": "12",
        "value": "$5.5"
    }];
    // prepare the data
    var source =
    {
        datatype: "json",
        datafields: [
        { name: 'id' },
        { name: 'parentid' },
        { name: 'text' },
        { name: 'value' }
        ],
        id: 'id',
        localdata: data
    };

    var width  = this.getWidth();
    var height = this.getHeight();

    var dataAdapter = new $.jqx.dataAdapter(source);
    // create data adapter.
    // perform Data Binding.
    dataAdapter.dataBind();
    // get the treemap sectors. The first parameter is the item's id. The second parameter is the parent item's id. The 'items' parameter represents 
    // the sub items collection name. Each jqxTreeMap item has a 'label' property, but in the JSON data, we have a 'text' field. The last parameter 
    // specifies the mapping between the 'text' and 'label' fields.  
    var records = dataAdapter.getRecordsHierarchy('id', 'parentid', 'items', [{ name: 'text', map: 'label' }]);
    $(this.container).jqxTreeMap({
        width: width,
        height: height,
        source: records,
        baseColor: '#0afaaa',
        renderCallbacks: {
                        '*': function (element, data) 
                        {
                            if (!data.parent)
                            {
                                element.css({
                                    backgroundColor: '#fff',
                                    border: '1px solid #aaa'
                                });
                            }
                            else 
                            {
                                element.css({
                                    color: 'White'
                                });
                            }
                        }
                    }
    });
}

this.setBaseColor = function(color)
{
    $(this.container).jqxTreeMap({baseColor: color}); 
}

